        TableCell cell12 = new TableCell();
        TableCell cell13 = new TableCell();
        TableCell cell14 = new TableCell();

        cell11.Text = fildeName;
        cell11.Width = 250;
        cell12.Text = receiptvlaue;
        cell12.Width = 350;
        cell13.Text = latestexpiredvalue;
        cell13.Width = 350;
        cell14.Text = string.Compare(receiptvlaue, latestexpiredvalue).ToString();
        cell14.Width = 50;
        
        TableRow row1 = new TableRow();
        row1.Cells.Add(cell11);
        row1.Cells.Add(cell12);
        row1.Cells.Add(cell13);
        row1.Cells.Add(cell14);
        row1.BorderWidth = 1;
        return row1;
    }
